'use strict';

var tap = module.exports = require('tap');
var sinon = require('sinon');

tap.beforeEach(function (done) {
    this.sandbox = sinon.sandbox.create({
        injectInto: this,
        properties: ['spy', 'stub', 'mock']
    });
    done();
});

tap.afterEach(function (done) {
    this.sandbox.restore();
    done();
});
